components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 0}}
        <script>
            var currencyToken = '{{cart.sub_total.formatted}}'.replace(/[\d,.\s]/g, "");
        </script>
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">
          {{lang 'common.currency_name' name=currency_selector.active_currency_name}} (<script>document.write(currencyToken);</script>) <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
        </a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>

                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>

{{/if}}

