components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 0}}
        <script>
            var currencyToken = '{{cart.sub_total.formatted}}'.replace(/[\d,.\s]/g, "");
        </script>
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">
          {{lang 'common.currency_name' name=currency_selector.active_currency_name}} (<script>document.write(currencyToken);</script>) <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
        </a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>

                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>

{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

pages/account/add-address:
{{#partial "page"}}

    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#if forms.address.address_id}}
        <h2 class="page-heading">{{lang 'forms.address.edit.heading'}}</h2>
    {{else}}
        <h2 class="page-heading">{{lang 'forms.address.add.heading'}}</h2>
    {{/if}}

    {{> components/account/navigation account_page='addresses'}}

    <div class="account account--fixed">

        {{#if forms.address.error}}
            {{> components/common/alert-error forms.address.error}}
        {{/if}}

        <div class="account-body">

            <form action="{{forms.address.action}}" data-address-form class="form" method="post">
                {{#if forms.address.address_id }}
                    <input type="hidden" name="shipid" value="{{forms.address.address_id}}">
                {{/if}}
                <fieldset class="form-fieldset">
                    <div class="form-row form-row--half">
                        {{#each forms.address.shipping_fields }}
                            {{{dynamicComponent 'components/common/forms'}}}
                        {{/each}}
                    </div>
                </fieldset>
                <div class="form-actions">
                    <input type="submit" class="button button--primary" value="{{lang 'forms.address.submit_value'}}">
                    <a href="{{urls.account.addresses}}" class="button">{{lang 'common.cancel'}}</a>
                    {{inject 'required' (lang 'common.required')}}
                    {{inject 'state_error' (lang 'errors.state_error')}}
                </div>
            </form>

        </div>
    </div>

{{/partial}}
{{> layout/base}}
